      close(3)
cc    so2.inp - Josh Kas
      open (file='s02.inp', unit=3, status='unknown',iostat=ios)
        write(3,10) 'mso2conv, ipse, ipsk'
        write(3,20)  mso2conv, ipse, ipsk
